import RajasthanDes from "../../images/RajasthanDes.jpg";
import Jaipur from "../../images/jaipur.jpg";
import Jodhpur from "../../images/jodhpur.jpg";
import Jaisalmer from "../../images/jaisalmer.jpg";
import Udaipur from "../../images/udaipur.jpg";
import "../dropdown.css";
import { Row, Col } from "react-bootstrap";

import React, { Component } from "react";
import ReactCardCarousel from "react-card-carousel";

const stylObj = {
  position: "relative",
  height: "100vh",
  width: "100%",
  display: "flex",
  flex: 1,
  justifyContent: "center",
  alignItems: "middle",
};
export default class MyCarousel extends Component {
  //   get CONTAINER_STYLE() {
  //     return {
  //       position: "relative",
  //       height: "100vh",
  //       width: "100%",
  //       display: "flex",
  //       flex: 1,
  //       justifyContent: "center",
  //       alignItems: "middle",
  //     };
  //   }
  state = {
    background: "null",
    content: {
      title: "null",
      desrciption: "null",
    },
  };
  static get CARD_STYLE() {
    return {
      backgroundColor: "white",
      height: "25rem",
      width: "20rem",
      color: "white",
      fontFamily: "sans-serif",
      fontSize: "1rem",
      textTransform: "uppercase",
      borderRadius: "13px",
      boxSizing: "border-box",
    };
  }
  static get CARD_IMAGE_STYLING() {
    return {
      position: "absolute",
      width: "20rem",
      height: "25rem",
    };
  }
  afterChange(idx) {
    console.log("index", idx);
  }
  render() {
    return (
      <>
        <div>
          <img className="DestImage" src={RajasthanDes} alt="raj"></img>
        </div>
        <Row>
          <Col className="destLeftSection pl-5" xs={5}>
            {this.state.content.title}
            <Row className="no-gutters about p-2 ">
              {this.state.content.description}
            </Row>
          </Col>

          <Col className="carousel">
            <div style={stylObj}>
              <ReactCardCarousel
                spread="wide"
                autoplay={true}
                autoplay_speed={2500}
                afterChange={this.afterChange}
              >
                <div style={MyCarousel.CARD_STYLE}>
                  <Row className="no-gutters">
                    <div>
                      <img
                        style={MyCarousel.CARD_IMAGE_STYLING}
                        alt="raj"
                        src={Jaipur}
                      ></img>
                    </div>
                    <Row className=" textPart no-gutters">
                      <Row className="no-gutters cardHeader">Jaipur Tour</Row>
                      <Row className="no-gutters cardInfo">
                        <p>
                          Umwittert getäuscht sonst nicht einst mich aus geneigt
                          die getäuscht. Die geisterreich schwebet an und blick
                          längst mich, und folgenden.
                        </p>
                        <button className="cardButton">Book now!</button>
                      </Row>
                    </Row>
                  </Row>
                </div>
                <div style={MyCarousel.CARD_STYLE}>
                  <Row className="no-gutters">
                    <div>
                      <img
                        style={MyCarousel.CARD_IMAGE_STYLING}
                        alt="raj"
                        src={Udaipur}
                      ></img>
                      <Row className=" textPart no-gutters">
                        <Row className="no-gutters cardHeader">
                          Udaipur Tour
                        </Row>
                        <Row className="no-gutters cardInfo">
                          <p>
                            Umwittert getäuscht sonst nicht einst mich aus
                            geneigt die getäuscht. Die geisterreich schwebet an
                            und blick längst mich, und folgenden.
                          </p>
                        </Row>
                      </Row>
                    </div>
                  </Row>
                </div>
                <div style={MyCarousel.CARD_STYLE}>
                  <Row className="no-gutters">
                    <div>
                      <img
                        style={MyCarousel.CARD_IMAGE_STYLING}
                        alt="raj"
                        src={Jaisalmer}
                      ></img>
                      <Row className=" textPart no-gutters">
                        <Row className="no-gutters cardHeader">
                          Jaisalmer Tour
                        </Row>
                        <Row className="no-gutters cardInfo">
                          <p>
                            Umwittert getäuscht sonst nicht einst mich aus
                            geneigt die getäuscht. Die geisterreich schwebet an
                            und blick längst mich, und folgenden.
                          </p>
                        </Row>
                      </Row>
                    </div>
                  </Row>
                </div>
                <div style={MyCarousel.CARD_STYLE}>
                  <Row className="no-gutters">
                    <div>
                      <img
                        style={MyCarousel.CARD_IMAGE_STYLING}
                        alt="raj"
                        src={Jodhpur}
                      ></img>
                      <Row className=" textPart no-gutters">
                        <Row className="no-gutters cardHeader">
                          Jodhpur Tour
                        </Row>
                        <Row className="no-gutters cardInfo">
                          <p>
                            Umwittert getäuscht sonst nicht einst mich aus
                            geneigt die getäuscht. Die geisterreich schwebet an
                            und blick längst mich, und folgenden.
                          </p>
                        </Row>
                      </Row>
                    </div>
                  </Row>
                </div>
              </ReactCardCarousel>
            </div>
          </Col>
        </Row>
      </>
    );
  }
}
