import RajasthanDes from "../../images/RajasthanDes.jpg";
import "../dropdown.css";
import { Row, Col } from "react-bootstrap";
import data from "./data";
import React, { Component } from "react";
import ReactCardCarousel from "react-card-carousel";
const stylObj = {
  position: "relative",
  height: "100vh",
  width: "100%",
  display: "flex",
  flex: 1,
  justifyContent: "center",
  alignItems: "middle",
};
export default class MyCarousel extends Component {
  //   get CONTAINER_STYLE() {
  //     return {
  //       position: "relative",
  //       height: "100vh",
  //       width: "100%",
  //       display: "flex",
  //       flex: 1,
  //       justifyContent: "center",
  //       alignItems: "middle",
  //     };
  //   }
  state = {
    backgroundImage: "null",
    content: {
      title: "null",
      desrciption: "null",
    },
  };
  static get CARD_STYLE() {
    return {
      backgroundColor: "white",
      height: "25rem",
      width: "20rem",
      color: "white",
      fontFamily: "sans-serif",
      fontSize: "1rem",
      textTransform: "uppercase",
      borderRadius: "13px",
      boxSizing: "border-box",
    };
  }
  static get CARD_IMAGE_STYLING() {
    return {
      position: "absolute",
      width: "20rem",
      height: "25rem",
    };
  }
  afterChange(idx) {
    console.log("index", idx);
    if (idx == undefined) {
      this.setState({
        backgroundImage: data.cities[0].image,
        content: {
          title: data.cites[0].header,
          description: data.cites[0].info,
        },
      });
    }
  }
  render() {
    return (
      <>
        <div>
          <div className="DestImage">{this.state.backgroundImage}</div>
        </div>
        <Row>
          <Col className="destLeftSection pl-5" xs={5}>
            {this.state.content.title}
            <Row className="no-gutters about p-2 ">
              {this.state.content.description}
            </Row>
          </Col>

          <Col className="carousel">
            <div style={stylObj}>
              <ReactCardCarousel
                spread="wide"
                autoplay={true}
                autoplay_speed={2500}
                afterChange={this.afterChange}
              >
                {data.cities.map((city, index) => {
                  return (
                    <div style={MyCarousel.CARD_STYLE}>
                      <Row className="no-gutters">
                        <div>{city.image}</div>
                        <Row className=" textPart no-gutters">
                          <Row className="no-gutters cardHeader">
                            {city.header}
                          </Row>
                          <Row className="no-gutters cardInfo">
                            <p>{city.info}</p>
                            <button className="cardButton">Book now!</button>
                          </Row>
                        </Row>
                      </Row>
                    </div>
                  );
                })}
              </ReactCardCarousel>
            </div>
          </Col>
        </Row>
      </>
    );
  }
}
