import RajasthanDes from "../../images/RajasthanDes.jpg";
import Jaipur from "../../images/jaipur.jpg";
import Jodhpur from "../../images/jodhpur.jpg";
import Jaisalmer from "../../images/jaisalmer.jpg";
import Udaipur from "../../images/udaipur.jpg";
import "../dropdown.css";
import { Row, Col } from "react-bootstrap";

import React, { Component } from "react";
import ReactCardCarousel from "react-card-carousel";

export default class MyCarousel extends Component {
  static get CONTAINER_STYLE() {
    return {
      position: "relative",
      height: "100vh",
      width: "100%",
      display: "flex",
      flex: 1,
      justifyContent: "center",
      alignItems: "middle",
    };
  }

  static get CARD_STYLE() {
    return {
      height: "25rem",
      width: "20rem",
      background: "#52C0F5",
      color: "#FFF",
      fontFamily: "sans-serif",
      fontSize: "1rem",
      textTransform: "uppercase",
      borderRadius: "10px",
      boxSizing: "border-box",
    };
  }
  static get CARD_IMAGE_STYLING() {
    return {
      width: "20rem",
      height: "20rem",
    };
  }

  render() {
    return (
      <>
        <div>
          <img className="DestImage" src={RajasthanDes} alt="raj"></img>
        </div>
        <Row>
          <Col xs={5}>Texttt</Col>
          <Col>
            <div style={MyCarousel.CONTAINER_STYLE}>
              <ReactCardCarousel autoplay={true} autoplay_speed={2500}>
                <div style={MyCarousel.CARD_STYLE}>
                  <Row className="no-gutters">
                    <div>
                      <img
                        style={MyCarousel.CARD_IMAGE_STYLING}
                        alt="raj"
                        src={Jaipur}
                      ></img>
                    </div>
                    <Row className="no-gutters">
                      Little information about the tour
                    </Row>
                    <button>Book now!</button>
                  </Row>
                </div>
                {/* <div style={MyCarousel.CARD_STYLE}>
                  <Row className="no-gutters">
                    <div>
                      <img
                        style={MyCarousel.CARD_IMAGE_STYLING}
                        alt="raj"
                        src={Udaipur}
                      ></img>
                    </div>
                  </Row>
                </div>
                <div style={MyCarousel.CARD_STYLE}>
                  <Row className="no-gutters">
                    <div>
                      <img
                        style={MyCarousel.CARD_IMAGE_STYLING}
                        alt="raj"
                        src={Jaisalmer}
                      ></img>
                    </div>
                  </Row>
                </div>
                <div style={MyCarousel.CARD_STYLE}>
                  <Row className="no-gutters">
                    <div>
                      <img
                        style={MyCarousel.CARD_IMAGE_STYLING}
                        alt="raj"
                        src={Jodhpur}
                      ></img>
                    </div>
                  </Row>
                </div> */}
              </ReactCardCarousel>
            </div>
          </Col>
